'use strict';

// Init all plugin when document is ready 
$(document).on('ready', function() {
    // 0. Init console to avoid error
    var method;
    var noop = function () {};
    var methods = [
        'assert', 'clear', 'count', 'debug', 'dir', 'dirxml', 'error',
        'exception', 'group', 'groupCollapsed', 'groupEnd', 'info', 'log',
        'markTimeline', 'profile', 'profileEnd', 'table', 'time', 'timeEnd',
        'timeStamp', 'trace', 'warn'
    ];
    var length = methods.length;
    var console = (window.console = window.console || {});
    while (length--) {
        method = methods[length];
        // Only stub undefined methods.
        if (!console[method]) {
            console[method] = noop;
        }
    }


    // 1. Background image as data attribut
    var list = $('.bg-img');
    for (var i = 0; i < list.length; i++) {
        var src = list[i].getAttribute('data-image-src');
        list[i].style.backgroundImage = "url('" + src + "')";
        list[i].style.backgroundRepeat = "no-repeat";
        list[i].style.backgroundPosition = "center";
        list[i].style.backgroundSize = "cover";
    }
    // Background color as data attribut
    var list = $('.bg-color');
    for (var i = 0; i < list.length; i++) {
        var src = list[i].getAttribute('data-bgcolor');
        list[i].style.backgroundColor = src;
    }

    // 3. Show/hide menu when icon is clicked
    var menuItems = $('.page-menu');
    var menuIcon = $('.menu-icon,#menu-link');
    // Menu icon clicked
    menuIcon.on('click', function () {
        if (menuIcon.hasClass('menu-visible')) {
            menuIcon.removeClass('menu-visible');
        } else {
            menuIcon.addClass('menu-visible');
        }
        if (menuItems.hasClass('menu-visible')) {
            menuItems.removeClass('menu-visible');
        } else {
            menuItems.addClass('menu-visible');
        }
        return false;
    });
    // Hide menu after a menu item clicked
    $(".page-menu a").on('click',function(){
        if (menuItems.hasClass('menu-visible')) {
            menuIcon.removeClass('menu-visible');
            menuItems.removeClass('menu-visible');
        }
        return true;
    });

    // 5. Init video background
    $('.video-container video, .video-container object').maximage('maxcover');

    // 6. Products/Projects/items slider
    var swiper = new Swiper('.items-slide', {
        pagination: '.items-pagination',
        paginationClickable: '.items-pagination',
        nextButton: '.items-button-next',
        prevButton: '.items-button-prev',
        grabCursor: true,
        slidesPerView: '2',
        centeredSlides: false,
        spaceBetween: 64,
        breakpoints: {
            1360: {
                spaceBetween: 32,
                slidesPerView: 3
            },
            1024: {
                slidesPerView: 2,
            },
            800: {
                slidesPerView: 2,
                spaceBetween: 32
            },
            640: {
                slidesPerView: 1,
                spaceBetween: 32
            },
            440: {
                slidesPerView: 1,
                spaceBetween: 32
            }
        }
    });
    var swiperB = new Swiper('.blog-slide', {
        pagination: '.blog-pagination',
        paginationClickable: '.blog-pagination',
        nextButton: '.blog-button-next',
        prevButton: '.blog-button-prev',
        grabCursor: true,
        slidesPerView: '2',
        centeredSlides: false,
        spaceBetween: 64,
        breakpoints: {
            1360: {
                spaceBetween: 32,
                slidesPerView: 3
            },
            1024: {
                slidesPerView: 2,
            },
            800: {
                slidesPerView: 2,
                spaceBetween: 32
            },
            640: {
                slidesPerView: 1,
                spaceBetween: 32
            },
            440: {
                slidesPerView: 1,
                spaceBetween: 32
            }
        }
    });
    var keys = {37: 1, 38: 1, 39: 1, 40: 1};

    function preventDefault(e) {
        e = e || window.event;
        if (e.preventDefault)
            e.preventDefault();
        e.returnValue = false;
    }

    function preventDefaultForScrollKeys(e) {
        if (keys[e.keyCode]) {
            preventDefault(e);
            return false;
        }
    }

    function disableScroll() {
        if (window.addEventListener) // older FF
            window.addEventListener('DOMMouseScroll', preventDefault, false);
        window.onwheel = preventDefault; // modern standard
        window.onmousewheel = document.onmousewheel = preventDefault; // older browsers, IE
        window.ontouchmove  = preventDefault; // mobile
        document.onkeydown  = preventDefaultForScrollKeys;
    }

    function enableScroll() {
        if (window.removeEventListener)
            window.removeEventListener('DOMMouseScroll', preventDefault, false);
        window.onmousewheel = document.onmousewheel = null;
        window.onwheel = null;
        window.ontouchmove = null;
        document.onkeydown = null;
    }
    // 7. Init fullPage.js plugin
    var pageSectionDivs = $('.fullpg .section');
    var headerLogo = $('.header-top .logo');
    var bodySelector = $('body');
    var sectionSelector = $('.section');
    var slideElem = $('.slide');
    var arrowElem = $('.p-footer .arrow-d');
    var pageElem = $('.section');
    var pageSections = [];
    var pageAnchors = [];
    var nextSectionDOM;
    var nextSection;
    var fpnavItem;
    var mainPage = $('#mainpage');
    // Get sections name
    for (var i = 0; i < pageSectionDivs.length; i++) {
        pageSections.push(pageSectionDivs[i]);
    }
    window.asyncEach(pageSections, function(pageSection , cb){
        var anchor = pageSection.getAttribute('data-section');
        pageAnchors.push(anchor + "");
        cb();
    }, function(err){
        // Init plugin
        if(mainPage.height()){
            // config fullpage.js
            mainPage.fullpage({
                menu: '#qmenu',
                anchors: pageAnchors,
                scrollingSpeed: 1000,
                responsiveWidth: 601,
                normalScrollElements: '#leftClone, #leftCloneS',
                css3: true,
                scrollOverflow: true,
                navigation: true,
                onLeave: function(index, nextIndex, direction){
                    overScrollA = 0;
                    overScrollS = 0;
                    // Behavior when a full page is leaved
                    arrowElem.addClass('gone');
                    pageElem.addClass('transition');
                    slideElem.removeClass('transition');
                    pageElem.removeClass('transition');
                    // disableScroll(function () {
                    //     enableScroll();
                    // });
                    // $.fn.fullpage.setAllowScrolling(false, 'up, down');
                    nextSectionDOM = sectionSelector[(nextIndex-1)];
                    nextSection = $(nextSectionDOM);
                    if(nextSection.hasClass('dark-bg')){
                        bodySelector.addClass('dark-section');
                        headerLogo.addClass('dark-bg');
                    }
                    else{
                        bodySelector.removeClass('dark-section');
                        headerLogo.removeClass('dark-bg');
                    }
                },
                afterLoad: function(anchorLink, index){
                    // Behavior after a full page is loaded
                    // manage dark bg for active section
                    if ($('.section.active').hasClass('js-left-light')) {
                        bodySelector.addClass('left-light');
                        headerLogo.addClass('dark-bg');
                    } else {
                        bodySelector.removeClass('left-light');
                        headerLogo.removeClass('dark-bg');
                    }

                    //set text value of fpnav
                    fpnavItem = $("#fp-nav ul li a.active");
                    if (!fpnavItem.children("span").hasClass("title")) {
                        // Init text content
                        fpnavItem.append('<span class="title">' + $(".section.active .page-title").text() +
                            '</span>')
                    }
                }

            });

        }
    });
    // Scroll to fullPage.js next/previous section
    $('.s-footer a.down').on('click', function () {
        $.fn.fullpage.moveSectionDown();
    });
    $('.s-footer a.up').on('click', function () {
        $.fn.fullpage.moveSectionUp();
    });


    if($(window).width() > 767) {
        $('.about_scroll').clone().attr('id', 'leftClone').css({
            'position': 'absolute',
            'top': 0,
            left: 0,
            width: '100%',
            overflow: 'scroll',
            "overlow-y": 'hidden',
            opacity: 0,
            "max-height": $('.about_scroll').height(),
        }).appendTo('.table_about');
        $('#leftClone').removeClass('anim');
        $('#leftClone').scroll(function () {
            $('.about_scroll').scrollTop($(this).scrollTop());
        });
        $('.services_scroll').clone().attr('id', 'leftCloneS').css({
            'position': 'absolute',
            'top': 0,
            left: 0,
            width: '100%',
            overflow: 'scroll',
            "overlow-y": 'hidden',
            opacity: 0,
            "max-height": $('.services_scroll').height(),
        }).appendTo('.table_services');
        $('#leftCloneS').removeClass('anim');
        $('#leftCloneS').scroll(function () {
            $('.services_scroll').scrollTop($(this).scrollTop());
        });
        var overScrollA = 0;
        var overScrollS = 0;
        $('.about_scroll').on('mousewheel', function (event) {
            console.log($('.about_scroll').scrollTop());
            if(event.deltaY == 1){
                if($('.about_scroll').scrollTop() == 0) {
                    overScrollA++;
                    if (overScrollA >= 6) {
                        overScrollA = 0;
                        $.fn.fullpage.moveTo(1);
                      //  $('#leftClone').scrollTop() - 6;
                    }
                }
            }
            else if($('.about_scroll')[0].scrollHeight*1 - $('.about_scroll').scrollTop()*1 ==  ($('.about_scroll').outerHeight())*1){
                overScrollA--;
                if(overScrollA <= -6){
                    overScrollA = 0;
                    $.fn.fullpage.moveTo(3);
                   // $('#leftClone').scrollTop() + 6;
                }
            }
        });
        $('.services_scroll').on('mousewheel', function (event) {
            if(event.deltaY == 1){
                if ($('.services_scroll').scrollTop() == 0) {
                    overScrollS++;
                    if (overScrollS >= 6) {
                        overScrollS = 0;
                        $.fn.fullpage.moveTo(2);
                       // $('#leftCloneS').scrollTop() - 6;
                    }
                }
            }
            else if ($('.services_scroll')[0].scrollHeight * 1 - $('.services_scroll').scrollTop() * 1 == ($('.services_scroll').outerHeight()) * 1) {
                overScrollS--;
                if (overScrollS <= -6) {
                    overScrollS = 0;
                    $.fn.fullpage.moveTo(4);
                   // $('#leftCloneS').scrollTop() + 6;
                }
            }
        });
    }




});


// Page Loader : hide loader when all are loaded
$(window).on('load', function(){
    jQuery(function(){
        jQuery("#bgndVideo").YTPlayer();
    });
    $('#page-loader').addClass('p-hidden');
    $('.section').addClass('anim');
});
